mapper.select:
SELECT count(1)
FROM tb_member
WHERE member_id = #{accountId}

mapper.update:
UPDATE tb_member
member_name = #{accountName}, member_birth = #{accountBirth}, member_email = #{accountEmail}, update_id = #{accountId}, update_date = CURRENT_TIMESTAMP()
,member_pw = #{accountPwd}
where member_idx = #{memberIdx}

UPDATE tb_member
SET member_use_yn = 'N'
WHERE member_idx = #{memberIdx}

mapper.select:
SELECT member_id
FROM tb_member
WHERE member_name = #{memberName}
AND member_email = #{memberEmail}
AND member_use_yn = 'Y'

SELECT member_idx
FROM tb_member
WHERE member_id = #{memberId}
AND member_name = #{memberName}
AND member_email = #{memberEmail}

SELECT count(member_idx)
FROM tb_member
WHERE member_id = #{memberId}
AND member_name = #{memberName}
AND member_email = #{memberEmail}

mapper.update:
UPDATE tb_member
SET member_pw = #{memberPw}
WHERE member_idx = #{memberIdx}
