mapper.select:
SELECT count(1)
FROM tb_member
WHERE member_id = #{accountId}

mapper.update:
UPDATE tb_member
member_name = #{accountName}, member_birth = #{accountBirth}, member_email = #{accountEmail}, update_id = #{accountId}, update_date = CURRENT_TIMESTAMP()
,member_pw = #{pwd}
where member_idx = #{memberIdx}
